        public static string LogPath
        {
            set
            {
                _eventLogFilePath = Path.Combine(value, "Nl.Aet.Cid.Client.Sdk.Test.Events.log");
                _commandLogFilePath = Path.Combine(value, "Nl.Aet.Cid.Client.Sdk.Test.Commands.log");
                _synclock = new object();
            }
        }

        public static void DisplayEvent(string eventDisplayString)
        {
            lock (_synclock)
            {
                File.AppendAllText(_eventLogFilePath, eventDisplayString);
            }
        }

        public static void DisplayCommand(string commandDisplayString)
        {
            lock (_synclock)
            {
                File.AppendAllText(_commandLogFilePath, commandDisplayString);
            }
        }

        public static string HashSha256(string value)
        {
            var stringData = string.Empty;

            using (var hash = SHA256.Create())
            {
                var encoding = Encoding.UTF8;
                var result = hash.ComputeHash(encoding.GetBytes(value));

                stringData = Convert.ToBase64String(result);
            }
            return stringData;
        }

        public static string HashSha1(string value)
        {
            var stringData = string.Empty;

            using (var hash = SHA1.Create())
            {
                var encoding = Encoding.UTF8;
                var result = hash.ComputeHash(encoding.GetBytes(value));

                stringData = Convert.ToBase64String(result);
            }
            return stringData;
        }

        

        /// <summary>
        /// This method is for validate the user input to be an number.
        /// </summary>
        /// <returns>the number inserted if valid, -1 otherwise</returns>
        public static int ReadIntInput()
        {
            int number = -1;

            try
            {
                string input = Console.ReadLine();
                Int32.TryParse(input, out number);
            }
            catch (Exception)
            {

                Console.WriteLine("\nInvalid input option, please insert an integer number.");
            }

            return number;
        }
